package uz.gita.contact_app_with_firebase.diimport dagger.Bindsimport dagger.Moduleimport dagger.hilt.InstallInimport dagger.hilt.android.components.ViewModelComponentimport uz.gita.contact_app_with_firebase.presenter.screens.add.AddContractimport uz.gita.contact_app_with_firebase.presenter.screens.add.AddDirectionimport uz.gita.contact_app_with_firebase.presenter.screens.main.MainContractimport uz.gita.contact_app_with_firebase.presenter.screens.main.MainDirectionimport uz.gita.contact_app_with_firebase.presenter.screens.update.UpdateContractimport uz.gita.contact_app_with_firebase.presenter.screens.update.UpdateDirection@Module@InstallIn(ViewModelComponent::class)interface DirectionModule {    @Binds    fun bindsMainDirection(impl: MainDirection): MainContract.MainDirection    @Binds    fun bindsAddDirection(impl: AddDirection): AddContract.AddDirection    @Binds    fun bindsUpdateDirection(impl: UpdateDirection): UpdateContract.UpdateDirection}