package uz.gita.contact_app_with_firebase.diimport com.google.firebase.Firebaseimport com.google.firebase.firestore.FirebaseFirestoreimport com.google.firebase.storage.StorageReferenceimport com.google.firebase.storage.storageimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class FirebaseModule {    @[Provides Singleton]    fun providesFireStore(): FirebaseFirestore =        FirebaseFirestore.getInstance()    @[Provides Singleton]    fun providesStorage(): StorageReference = Firebase.storage.reference}