package uz.gita.contact_app_with_firebase.domain.usecase.implimport uz.gita.contact_app_with_firebase.domain.repository.AppRepositoryimport uz.gita.contact_app_with_firebase.domain.usecase.DeleteUseCaseimport javax.inject.Injectclass DeleteUseCaseImpl @Inject constructor(    private val appRepository: AppRepository,) : DeleteUseCase {    override fun invoke(id: String) = appRepository.deleteContact(id)}