package uz.gita.contact_app_with_firebase.domain.usecase.implimport android.net.Uriimport uz.gita.contact_app_with_firebase.domain.repository.AppRepositoryimport uz.gita.contact_app_with_firebase.domain.usecase.UpdateUseCaseimport javax.inject.Injectclass UpdateUseCaseImpl @Inject constructor(    private val appRepository: AppRepository,) : UpdateUseCase {    override fun invoke(id: String,image: String, name: String, phone: String) =        appRepository.updateContact(id,image, name, phone)}