package uz.gita.contact_app_with_firebase.navigationimport kotlinx.coroutines.flow.MutableSharedFlowimport javax.inject.Injectimport javax.inject.Singleton@Singletonclass AppNavigatorDispatcher @Inject constructor() : AppNavigator, AppNavigatorHandler {    override val uiNavigator = MutableSharedFlow<NavigatorArgs>()    private suspend fun myNav(navigatorArgs: NavigatorArgs) {        uiNavigator.emit(navigatorArgs)    }    override suspend fun openWithSave(screen: Screen) = myNav {        push(screen)    }    override suspend fun openWithoutSave(screen: Screen) = myNav {        replace(screen)    }    override suspend fun back() = myNav {        pop()    }}