package uz.gita.contact_app_with_firebase.presenter.screens.addimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport uz.gita.contact_app_with_firebase.domain.usecase.InsertContactUseCaseimport javax.inject.Inject@HiltViewModelclass AddViewModel @Inject constructor(    private val insertContactUseCase: InsertContactUseCase,    private val direction: AddContract.AddDirection,) : AddContract.ViewModel, ViewModel() {    override fun onEventDispatcher(intent: AddContract.Intent) {        when (intent) {            is AddContract.Intent.AddContact -> {                viewModelScope.launch(Dispatchers.IO) {                    insertContactUseCase.invoke( intent.image, intent.name, intent.phone)                }            }            AddContract.Intent.MoveToMain -> {                viewModelScope.launch {                    direction.openMain()                }            }        }    }}