package uz.gita.contact_app_with_firebase.presenter.screens.mainimport uz.gita.contact_app_with_firebase.data.models.ContactParamimport uz.gita.contact_app_with_firebase.navigation.AppNavigatorimport uz.gita.contact_app_with_firebase.presenter.screens.add.AddScreenimport uz.gita.contact_app_with_firebase.presenter.screens.update.UpdateScreenimport javax.inject.Injectclass MainDirection @Inject constructor(    private val appNavigator: AppNavigator,) : MainContract.MainDirection {    override suspend fun openUpdateScreen(contactParam: ContactParam) {        appNavigator.openWithSave(UpdateScreen(contactParam))    }    override suspend fun openAddScreen() {        appNavigator.openWithSave(AddScreen())    }}