package uz.gita.contact_app_with_firebase.presenter.screens.updateimport android.net.Uriinterface UpdateContract {    interface ViewModel {        fun onEventDispatcher(intent: Intent)    }    interface Intent {        data class UpdateContact(            val id: String,            val image: String,            val name: String,            val phone: String,        ) : Intent        object MoveToMainScreen : Intent    }    interface UpdateDirection {        suspend fun openMainScreen()    }}