package uz.gita.contact_app_with_firebase.presenter.screens.updateimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.launchimport uz.gita.contact_app_with_firebase.domain.usecase.UpdateUseCaseimport javax.inject.Inject@HiltViewModelclass UpdateViewModel @Inject constructor(    private val updateUseCase: UpdateUseCase,    private val direction: UpdateContract.UpdateDirection,) : UpdateContract.ViewModel, ViewModel() {    override fun onEventDispatcher(intent: UpdateContract.Intent) {        when (intent) {            is UpdateContract.Intent.UpdateContact -> {                viewModelScope.launch {                    updateUseCase.invoke(intent.id, intent.image,intent.name, intent.phone)                }            }            UpdateContract.Intent.MoveToMainScreen -> {                viewModelScope.launch {                    direction.openMainScreen()                }            }        }    }}